def send_email(sender, password, receiver, subject, body, attachment_path):
    try:
        msg = EmailMessage()
        msg['Subject'] = subject
        msg['From'] = sender
        msg['To'] = receiver
        msg.set_content(body)
        with open(attachment_path, 'rb') as f:
            data = f.read()
            msg.add_attachment(data, maintype='image', subtype='png', filename='encrypted_image.png')
        with smtplib.SMTP_SSL('smtp.gmail.com', 465) as smtp:
            smtp.login(sender, password)
            smtp.send_message(msg)
        messagebox.showinfo("Success", "Encrypted Image & Password Sent via Email!")
        log_debug("Email sent successfully.")
    except Exception as e:
        messagebox.showerror("Email Error", str(e))
        log_debug(f"Email send error: {e}")

loader_window = None

def show_loader(message="Processing..."):
    global loader_window
    loader_window = tk.Toplevel()
    loader_window.title("Please wait")
    loader_window.geometry("300x100")
    loader_window.resizable(False, False)
    loader_window.grab_set()
    tk.Label(loader_window, text=message, font=("Arial", 12)).pack(pady=10)
    pb = ttk.Progressbar(loader_window, mode='indeterminate')
    pb.pack(pady=10, padx=20, fill='x')
    pb.start()

def hide_loader():
    global loader_window
    if loader_window:
        loader_window.destroy()
        loader_window = None

def perform_encryption():
    win = tk.Toplevel()
    win.title("Encrypt Options")
    win.geometry("500x550")

    tk.Label(win, text="Select Image (.png/.jpg/.jpeg):").pack(pady=5)
    img_entry = tk.Entry(win, width=50)
    img_entry.pack()
    tk.Button(win, text="Browse", command=lambda: img_entry.insert(0, filedialog.askopenfilename(filetypes=[("Images", "*.png *.jpg *.jpeg")]))).pack(pady=5)

    tk.Label(win, text="Choose to Hide:").pack()
    option = tk.StringVar(value="Text")
    tk.Radiobutton(win, text="Text", variable=option, value="Text", command=lambda: toggle_fields()).pack()
    tk.Radiobutton(win, text="File", variable=option, value="File", command=lambda: toggle_fields()).pack()

    msg_label = tk.Label(win, text="Enter your Message:")
    msg_label.pack()
    msg_entry = tk.Entry(win, width=50)
    msg_entry.pack()

    file_frame = tk.Frame(win)
    file_btn = tk.Button(file_frame, text="Select File", command=lambda: select_file())
    file_btn.pack(side=tk.LEFT)
    file_frame.pack()
    file_frame.pack_forget()

    tk.Label(win, text="Sender Gmail:").pack()
    sender_entry = tk.Entry(win, width=50)
    sender_entry.pack()

    tk.Label(win, text="Sender SMTP Password:").pack()
    pass_entry = tk.Entry(win, show="*", width=50)
    pass_entry.pack()

    tk.Label(win, text="Receiver Gmail:").pack()
    recv_entry = tk.Entry(win, width=50)
    recv_entry.pack()

    def toggle_fields():
        msg_entry.delete(0, tk.END)
        if option.get() == "Text":
            msg_label.config(text="Enter your Message:")
            file_frame.pack_forget()
        else:
            msg_label.config(text="Selected File:")
            file_frame.pack()

    def select_file():
        file_path = filedialog.askopenfilename()
        if file_path:
            img_path = img_entry.get()
            if not os.path.exists(img_path):
                messagebox.showerror("Error", "Upload Image First")
                return
            try:
                with open(file_path, 'rb') as f:
                    data = f.read().hex() + "END"
                msg_entry.delete(0, tk.END)
                msg_entry.insert(0, file_path)
            except:
                messagebox.showerror("Error", "Unable to read file.")

    def process():
        show_loader("Encrypting & Sending Email...")
        threading.Thread(target=encrypt_image_logic).start()

    def encrypt_image_logic():
        img_path = img_entry.get()
        msg = msg_entry.get()
        if not img_path.lower().endswith(('.png', '.jpg', '.jpeg')):
            hide_loader()
            messagebox.showerror("Error", "Use PNG/JPG image.")
            return
        if not os.path.exists(img_path) or (option.get() == "File" and not os.path.exists(msg)):
            hide_loader()
            messagebox.showerror("Error", "Invalid file selection.")
            return

        password = generate_password()
        log_debug(f"Generated Password: {password}")
        img = Image.open(img_path)
        encoded = img.copy()
        width, height = img.size
        payload = password + "::"

        if option.get() == "Text":
            payload += msg + chr(0)
        else:
            try:
                file_name = os.path.basename(msg)
                with open(msg, 'rb') as f:
                    file_data = f.read().hex()
                payload += file_name + "::" + file_data + "END"
            except:
                hide_loader()
                messagebox.showerror("Error", "Failed to read selected file.")
                return

        if len(payload) * 8 > width * height * 3:
            hide_loader()
            messagebox.showerror("Error", "Payload too large.")
            return

        i = 0
        for row in range(height):
            for col in range(width):
                pixel = list(img.getpixel((col, row)))
                for n in range(3):
                    if i < len(payload) * 8:
                        char_index = i // 8
                        bit_index = 7 - (i % 8)
                        bit = (ord(payload[char_index]) >> bit_index) & 1
                        pixel[n] = (pixel[n] & ~1) | bit
                        i += 1
                encoded.putpixel((col, row), tuple(pixel))
                if i >= len(payload) * 8:
                    break
            if i >= len(payload) * 8:
                break

        encoded.save("encrypted_image.png")
        with open(PASSWORD_FILE, 'w') as f:
            f.write(password)

        email_body = f"The password to decrypt the image is: {password}\nUse it in the app's decryption section."
        send_email(sender_entry.get(), pass_entry.get(), recv_entry.get(), "Encrypted Image & Password", email_body, "encrypted_image.png")
        log_debug("Encryption successful and image saved as encrypted_image.png")
        hide_loader()
        messagebox.showinfo("Success", "Encryption & Email Complete!")
        win.destroy()

    tk.Button(win, text="Encrypt", bg="red", fg="white", command=process).pack(pady=15)

def decode_message():
    win = tk.Toplevel()
    win.title("Decrypt Options")
    win.geometry("450x350")

    tk.Label(win, text="Upload Image to Decrypt (.png/.jpg/.jpeg):").pack(pady=5)
    img_entry = tk.Entry(win, width=50)
    img_entry.pack()
    tk.Button(win, text="Browse", command=lambda: img_entry.insert(0, filedialog.askopenfilename(filetypes=[("Images", "*.png *.jpg *.jpeg")]))).pack(pady=5)

    tk.Label(win, text="Enter Received Password:").pack(pady=5)
    user_pass_entry = tk.Entry(win, show="*", width=50)
    user_pass_entry.pack()

    def process():
        show_loader("Decrypting...")
        threading.Thread(target=decrypt_image_logic).start()

    def decrypt_image_logic():
        img_path = img_entry.get().strip()
        entered_pass = user_pass_entry.get().strip()
        log_debug(f"Decrypting image: {img_path}")

        if not img_path or not entered_pass:
            hide_loader()
            messagebox.showerror("Error", "All fields are required.")
            return

        try:
            img = Image.open(img_path)
        except Exception as e:
            hide_loader()
            messagebox.showerror("Error", f"Unable to open image: {e}")
            log_debug(f"Failed to open image: {e}")
            return

        width, height = img.size
        bits = []
        for row in range(height):
            for col in range(width):
                pixel = img.getpixel((col, row))
                for n in range(3):
                    bits.append(pixel[n] & 1)

        msg = ""
        for i in range(0, len(bits), 8):
            byte = bits[i:i+8]
            if len(byte) < 8:
                break
            char = chr(int("".join(map(str, byte)), 2))
            if char == chr(0):
                break
            msg += char
            if msg.endswith("END"):
                break

        if "::" not in msg:
            hide_loader()
            messagebox.showerror("Error", "Invalid or Corrupted Image")
            log_debug("Missing password delimiter '::' in extracted data.")
            return

        actual_pass, real_msg = msg.split("::", 1)
        log_debug(f"Extracted password: {actual_pass}")

        if entered_pass != actual_pass:
            hide_loader()
            messagebox.showerror("Error", "Incorrect Password!")
            log_debug("Incorrect password entered.")
            return

        if real_msg.endswith("END"):
            output_dir = "DecryptedFiles"
            os.makedirs(output_dir, exist_ok=True)
            try:
                file_name, hex_data = real_msg[:-3].split("::", 1)
                data = bytes.fromhex(hex_data)
                output_path = os.path.join(output_dir, file_name)
                with open(output_path, "wb") as f:
                    f.write(data)

                hide_loader()
                messagebox.showinfo("Decryption Successful", f"File saved to '{output_path}'")
                win.destroy()
                log_debug(f"Decrypted file saved to: {output_path}")

                if messagebox.askyesno("Open File", f"Do you want to open '{file_name}' now?"):
                    try:
                        if platform.system() == "Windows":
                            os.startfile(output_path)
                        elif platform.system() == "Darwin":
                            subprocess.call(["open", output_path])
                        else:
                            subprocess.call(["xdg-open", output_path])
                    except Exception as open_err:
                        log_debug(f"Could not auto-open file: {open_err}")
                        messagebox.showwarning("Open Failed", f"File saved, but could not be opened automatically.\nError: {open_err}")
            except Exception as e:
                hide_loader()
                messagebox.showerror("Error", "Decryption failed.")
                log_debug(f"Decryption failed: {e}")
        else:
            hide_loader()
            messagebox.showinfo("Decryption Successful", f"Message: {real_msg.rstrip(chr(0))}")
            log_debug(f"Decrypted message: {real_msg.rstrip(chr(0))}")

    tk.Button(win, text="Decrypt", bg="red", fg="white", command=process).pack(pady=15)

